import React, { useState } from "react";

const Body = (props) => {
  let name = props.myObj.name;
  let age = props.myObj.age;

  // Declare a new state variable, which we'll call "ageGiven"
  const [ageGiven, setAgeGiven] = useState(age);

  const ageIncreaseHandler = () => {
    // age += 10;
    // console.log(name + " is " + age);
    setAgeGiven(ageGiven + 10);
    console.log(name + " is " + ageGiven);
  };

  return (
    <div>
      <h1>Body</h1>
      <p>This is a new component!</p>
      <button onClick={ageIncreaseHandler}>Click to make {name} older!</button>
      <p>
        I am {name} and now I am {props.myObj.age} years old.{" "}
      </p>
    </div>
  );
};

export default Body;
